import java.util.Scanner;

public class For8393 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//A+B
		
		Scanner scan=new Scanner(System.in);
		int t=scan.nextInt();
		
		for(int i=0;i<t;i++) {
			int a=scan.nextInt();
			int b=scan.nextInt();
			System.out.println(a+b);
		}
	}
}
